import React from "react";
import styles from "./Cards.module.css";
import Countup from "react-countup";
import { Card, Typography, Grid } from "@material-ui/core";
import cx from "classnames";
import AppBar from "@material-ui/core/AppBar";

const Cards = ({ data: { confirmed, recovered, deaths, lastUpdate } }) => {
  if (!confirmed) {
    return "Loading..";
  }
  return (
    <div>
      <div>
        <div>
          <Typography variant="h5">
            <Typography variant="h5">Infected</Typography>
            <Countup
              start={0}
              end={confirmed.value}
              duration={2.5}
              separator=","
            />
          </Typography>
        </div>
      </div>
      <div className={styles.container}>
        <Grid container spacing={3} justify="center">
          <Grid
            item
            component={Card}
            style={{ width: "700px", paddingRight: "35px" }}
            xs={12}
            md={12}
            className={cx(styles.card)}
          >
            <AppBar position="static" className={cx(styles.card)}>
              <Typography variant="h5">
                <Typography variant="h5">Infected</Typography>
                <Countup
                  start={0}
                  end={confirmed.value}
                  duration={2.5}
                  separator=","
                />
              </Typography>
            </AppBar>
            <AppBar
              position="static"
              className={cx(styles.card, styles.infected)}
            >
              <Typography variant="h5">
                <Typography variant="h5">Recovered</Typography>
                <Countup
                  start={0}
                  end={recovered.value}
                  duration={2.5}
                  separator=","
                />
              </Typography>
            </AppBar>
            <AppBar
              position="static"
              className={cx(styles.card, styles.infected)}
            >
              <Typography variant="h5">
                <Typography variant="h5">Deaths</Typography>
                <Countup
                  start={0}
                  end={deaths.value}
                  duration={2.5}
                  separator=","
                />
              </Typography>
            </AppBar>
            <AppBar
              position="static"
              className={cx(styles.card, styles.infected)}
            >
              <Typography variant="h5">
                {new Date(lastUpdate).toDateString()}
              </Typography>
            </AppBar>
          </Grid>
        </Grid>
      </div>
    </div>
  );
};

export default Cards;
